import React, { Component } from 'react';
import axios from 'axios'; 
export default class sechome extends Component {
  constructor(props){
    super(props);

    this.state={
      posts:[]
    };
}
componentDidMount(){
  this.retrievePosts();
}

retrievePosts(){
  axios.get("/post").then(res =>{
    if(res.data.success){
      this.setState({
        posts:res.data.existingPost
      });
      console.log(this.state.posts)
    }
  });
}

onDelete = (id) => {
  axios.delete(`/post/delete/${id}`).then((res) =>{
    alert("Deleted Successfully!!");
    this.retrievePosts();
  })

}
filterData(post,searchkey){

  const result = post.filter((post) =>
  post.topic.includes(searchkey)
)

this.setState({post:result})
}

handleSearchArea = (e) =>{

  const searchkey= e.currentTarget.value;

  axios.get("/post").then(res =>{
    if(res.data.success){
      this.filterData(res.data.existingPosts,searchkey)
  
    }
  });
}
 
  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-lg-9 mt-2 mb-2">
            <h4>All Security Guards</h4>
            </div>
            <div className="col-lg-3 mt-2 mb-2">
              <input
              className="form-control"
              type="search"
              placeholder="search"
              onChange={this.handleSearchArea}>
                </input>
                </div>
                </div>
        <table class="table">
          <thead>
            <tr>
              <th scope="col">CID</th>
              <th scope="col">name</th>
              <th scope="col">nic</th>
              <th scope="col">email</th>
              <th scope="col">address</th>
              <th scope="col">age</th>
            </tr>
          </thead>   

              <tbody>
                {this.state.posts.map((post,index) =>(
                  <tr key={index}>
                    <th scope="row">{index+1}</th>
                      <td>{post.cid}</td>
                      <td>
                        <a href={`/post/${post._id}`} style={{textDecoration:'none'}}>
                        {post.name}</a></td>
                      <td>{post.nic}</td>
                      <td>{post.email}</td>
                      <td>{post.address}</td>
                      <td>{post.age}</td>
                      <td>
                        <a className="btn btn-warning" href={`/editsec/${post._id}`}>
                          <i className="fas fa-edit"></i>&nbsp;Edit
                        </a>
                      &nbsp;
                        <a className="btn btn-danger" href="#" onClick={() =>this.onDelete(post._id)}>
                          <i className="far fa-trash-alt">&nbsp;</i>&nbsp;Delete
                        </a>

                      </td>

                    </tr>


                ))}    

              </tbody>
        </table>
         <button className="btn btn-success"><a href="/addsec" style={{textDecoration:'none' , color:'white'}}>create new guard</a></button>
       </div>
        
      
    )
  }
}
