import axios from 'axios';
import React, { Component } from 'react';

export default class Createguard extends Component {
    constructor(props){
        super(props);
        this.state={
            cid:"",
            name:"",
            nic:"",
            email:"",
            address:"",
            age:""


        }
    }
    handleInputChange= (e) =>{
        const {name,value} = e.target;

        this.setState({
            ...this.state,
            [name]:value
        })
    }
    onSubmit = (e) =>{
        e.preventDefault() ;
        const {cid,name,nic,email,address,age} = this.state;

        const data={
            cid:cid,
            name:name,
            nic:nic,
            email:email,
            address:address,
            age:age
        }
        console.log(data);

        axios.post("/post/save",data).then((res)=>{
            if(res.data.success){
                this.setState(
                    {  
                    cid:"",
                    name:"",
                    nic:"",
                    email:"",
                    address:"",
                    age:""
                    }
                )
            }
        })
    }



    render() {
        return (
<div className="container">
  <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
    <div className="container-fluid">
      <a className="navbar-brand" href="/sechome" style={{fontSize:34,}}><b> Security Guards </b></a>
      <a className="btn navbar-btn btn-danger navbar-right" href="/">Logout</a>


      
      
      
    </div>
  </nav>
 

            <div className="col-md-8 mt-4 mx-auto">
            <h1 className="h3 mb-3 font-weight-normal">Add New Security Guard</h1>  
            <form className="needs-validation" noValidate>
                <div className="form-group" style={{marginBottom:'15px'}}>
                    <lable style={{marginBottom:'5px'}}> Guard id Number</lable>
                    <input type="text"
                    className="form-control"
                    name="cid"
                    placeholder="Enter Guard ID Number"
                    value={this.state.cid}
                    onChange={this.handleInputChange}/>
                </div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                    <lable style={{marginBottom:'5px'}}>Guard Name</lable>
                    <input type="text"
                    className="form-control"
                    name="name"
                    placeholder="Enter Guard Name"
                    value={this.state.name}
                    onChange={this.handleInputChange}/>
                </div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                    <lable style={{marginBottom:'5px'}}>NIC</lable>
                    <input type="text"
                    className="form-control"
                    name="nic"
                    placeholder="Enter NIC"
                    value={this.state.nic}
                    onChange={this.handleInputChange}/>
                </div>


                <div className="form-group" style={{marginBottom:'15px'}}>
                    <lable style={{marginBottom:'5px'}}>Email</lable>
                    <input type="text"
                    className="form-control"
                    name="email"
                    placeholder="Enter email"
                    value={this.state.email}
                    onChange={this.handleInputChange}/>
                </div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                    <lable style={{marginBottom:'5px'}}>address</lable>
                    <input type="text"
                    className="form-control"
                    name="address"
                    placeholder="Enter address"
                    value={this.state.address}
                    onChange={this.handleInputChange}/>
                </div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                    <lable style={{marginBottom:'5px'}}>age</lable>
                    <input type="text"
                    className="form-control"
                    name="age"
                    placeholder="Enter age"
                    value={this.state.age}
                    onChange={this.handleInputChange}/>
                </div>


                <button className="btn btn-success" type="submit" style={{marginTop:'15px'}} onClick={this.onSubmit}>
                    <i className="far fa-check-square"></i>
                    &nbsp; SAVE
                </button>

                </form>
                </div>
                </div>
        );
    }
}
