import React, { Component } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import * as emailjs from "emailjs-com"


export default class CreateReport extends Component {

   constructor(props){
        super(props);
        this.state={
            name:"",
            nic:"",
            age:"",
            sex:"",
            type:"",
            result:"",
            date:"",
            status:"",

            nameerr:"",
            nicerr:"",
            ageerr:"",
            typeerr:"",
            resulterr:"",
            dateerr:"",
        }
    }

    handleInputChange = (e) =>{
        const {name,value} = e.target;

        this.setState({
            ...this.state,
            [name]:value
        })
    }

    validate = () => {

      let nameerr = "";
      let nicerr="";
      let ageerr="";
      let typeerr="";
      let resulterr="";
      let dateerr="";

      //pattern = "/^[0]\[0-9]{9}$/"

      if (!this.state.name){
        nameerr = "Name cannot be blank";
      }
      if (!this.state.nic){
        nicerr = "NIC cannot be blank";
      }
      if (!this.state.age){
        ageerr = "Age cannot be blank";
      }
      if (!this.state.type){
        typeerr = "Type cannot be blank";
      }
      if (!this.state.result){
        resulterr = "Result cannot be blank";
      }
      if (!this.state.date){
        dateerr = "Date cannot be blank";
      }

      if (nameerr || nicerr || ageerr || typeerr ||resulterr || dateerr ){
          this.setState({nameerr,nicerr,ageerr,typeerr,resulterr,dateerr });
          return false;
      } 

      return true;
  };





    onSubmit = (e) =>{

        e.preventDefault();
        //emailjs
        emailjs
        .sendForm(
            "service_yf3x37q",
            "template_wij65an",
            "needs-validation",
            "user_czz8itgk7SZROsIajU0vA"

        )
        .then()
        .catch();
        this.setState({
            name:""
        });


        //end email jas

        const isValid = this.validate();
        if (isValid) {

        const {name,nic,age,sex,type,result,date,status} = this.state;

        const data = {
            name:name,
            nic:nic,
            age:age,
            sex:sex,
            type:type,
            result:result,
            date:date,
            status:status
        }

        console.log(data);

        axios.post("/lab/save",data).then((res) =>{
            if(res.data.success){
              //alert("Report Added Successfully !!")
              Swal.fire(
                'Added !!',
                'Report Added Successfully !!',
                'success'
              )

                this.setState(
                    {
                        name:"",
                         nic:"",
                         age:"",
                         sex:"",
                        type:"",
                         result:"",
                         date:"",
                        status:"",


                        nameerr:"",
                        nicerr:"",
                        ageerr:"",
                        typeerr:"",
                        resulterr:"",
                        dateerr:"",
                    }
                )
            }
        })

    }
  }

    render() {
        return (
            <div>

	<link href="https://fonts.googleapis.com/css?family=Montserrat:400,700" rel="stylesheet" />



	<link type="text/css" rel="stylesheet" href="css/labstyle.css" />


  <div>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <div className="container-fluid">

          <a className="navbar-brand" href="/labhome" style={{fontSize:34,}}><b> <span class="fa fa-vial" aria-hidden="false">  SAKURA Laboratory</span> </b></a>
          <a className="btn navbar-btn btn-danger navbar-right" href="/">Logout</a>



          </div>
          </nav>



              <div id="lab" class="section">
		<div class="section-center">
			<div class="container">
				<div class="row">
					<div class="lab-form">
						<div class="form-header">
							<h1>Create Report</h1>
						</div>


              <form className="needs-validation" noValidate>
  <div class="mb-3">
    <label for="name" class="form-label">Patient Name</label>
    <input type="text" class="form-control" name="name" required
     value={this.state.name} 
     pattern="[a-z A-Z.]+"
     onChange={this.handleInputChange} 
     placeholder="Enter Patient Name"
    /> 
  </div>
  {this.state.nameerr ? (<div style={{ fonysize:12, color:"red", paddingBottom:"5px", paddingLeft:"150px"}}>{this.state.nameerr}</div> ) : null}



  <div class="mb-3">
    <label for="nic" class="form-label">NIC</label>
    <input type="text" class="form-control" name="nic" required
