import React, { Component } from 'react';
import axios from 'axios';
export default class editguard extends Component {

    constructor(props){
        super(props);
        this.state={
            cid:"",
            name:"",
            nic:"",
            email:"",
            address:"",
            age:""


        }
    }
    handleInputChange= (e) =>{
        const {name,value} = e.target;

        this.setState({
            ...this.state,
            [name]:value
        })
    }
    onSubmit = (e) =>{
        
        e.preventDefault() ;
        const id = this.props.match.params.id;
        const {cid,name,nic,email,address,age} = this.state;

        const data={
            cid:cid,
            name:name,
            nic:nic,
            email:email,
            address:address,
            age:age
        }

        console.log(data)

        axios.put(`/post/update/${id}`,data).then((res)=>{
            if(res.data.success){
                alert("post updated successfully")
                this.setState(
                    {  
                    cid:"",
                    name:"",
                    nic:"",
                    email:"",
                    address:"",
                    age:""
                    }
                )
            }
        })
    }


    componentDidMount(){
        const id = this.props.match.params.id;
        axios.get(`/post/${id}`).then((res) =>{
            if(res.data.saccess){
                this.setState({
                    cid:res.data.post.name,
                    name:res.data.post.name,
                    nic:res.data.post.nic,
                    email:res.data.post.email,
                    address:res.data.post.address,
                    age:res.data.post.age
                });
                console.log(this.state.post);
            }


        });


    }

    render() {
        return (
            <div className="col-md-8 mt-4 mx-auto">
            <h1 className="h3 mb-3 font-weight-normal">Edit Security Guard</h1>  
            <form className="needs-validation" noValidate>
                <div className="form-group" style={{marginBottom:'15px'}}>
                    <label style={{marginBottom:'5px'}}> Guard id Number</label>
                    <input type="text"
                    className="form-control"
                    name="cid"
                    placeholder="Enter Drivers ID Number"
                    value={this.state.cid}
                    onChange={this.handleInputChange}
                    />
                </div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                    <label style={{marginBottom:'5px'}}>Guard Name</label>
                    <input type="text"
                    className="form-control"
                    name="name"
                    placeholder="Enter Guard Name"
                    value={this.state.name}
                    onChange={this.handleInputChange}/>
                </div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                    <label style={{marginBottom:'5px'}}>NIC</label>
                    <input type="text"
                    className="form-control"
                    name="nic"
                    placeholder="Enter NIC"
                    value={this.state.nic}
                    onChange={this.handleInputChange}/>
                </div>


                <div className="form-group" style={{marginBottom:'15px'}}>
                    <label style={{marginBottom:'5px'}}>Email</label>
                    <input type="text"
                    className="form-control"
                    name="email"
                    placeholder="Enter email"
                    value={this.state.email}
                    onChange={this.handleInputChange}/>
                </div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                    <label style={{marginBottom:'5px'}}>address</label>
                    <input type="text"
                    className="form-control"
                    name="address"
                    placeholder="Enter address"
                    value={this.state.address}
                    onChange={this.handleInputChange}/>
                </div>

                <div className="form-group" style={{marginBottom:'15px'}}>
                    <label style={{marginBottom:'5px'}}>age</label>
                    <input type="text"
                    className="form-control"
                    name="age"
                    placeholder="Enter age"
                    value={this.state.age}
                    onChange={this.handleInputChange}/>
                </div>


                <button className="btn btn-success" type="submit" style={{marginTop:'15px'}} onClick={this.onSubmit}>
                    <i className="far fa-check-square"></i>
                    &nbsp;  UPDATE
                </button>

                </form>
                </div>
        )
    }
}
