import React, { Component } from "react";
import axios from 'axios'
import lab from "../../models/lab";

export default class App extends Component {

constructor(props){
  super(props);
this.state={
  lab:[]
};

}

componentDidMount(){
  this.retriveReports();
}

retriveReports(){
  axios.get("http://localhost:8000/lab").then(res =>{

  if(res.data.success){
    this.setState({
      lab:res.data.existingLab
    });

    console.log(this.state.lab)
  }

  });
}

  render() {
    return(
      <div>
        <p>Laboratory Management System</p>
        <table>class="table"</table>
        <thead class="table">
                <tr>
                    <th scope="col">ID</th>
                    <th scope="col">Name</th>
                    <th scope="col">NIC</th>
                    <th scope="col">Age</th>
                    <th scope="col">Sex</th>
                    <th scope="col">Type</th>
                    <th scope="col">Results</th>
                    <th scope="col">Date</th>
                    <th scope="col">Status</th>
                    <th scope="col">Action</th>
                </tr>
            </thead>
            <tbody>
              {this.state.lab.map((lab,index) =>(
                <tr>
                  <th scope="row">{index+1}</th>
                  <td>{lab.name}</td>
                  <td>{lab.nic}</td>
                  <td>{lab.age}</td>
                  <td>{lab.sex}</td>
                  <td>{lab.type}</td>
                  <td>{lab.result}</td>
                  <td>{lab.date}</td>
                  <td>{lab.status}</td>
                  <td>
                    <a className="btn btn-warning" href="#">
                      <i className="fas fa-edit"></i>&nbsp;Edit
                    </a>
                    &nbsp;
                    <a className="btn btn-danger" href="#">
                      <i className="far fa-trash-alt"></i>&nbsp:Edit
                    </a>
                  </td>
                </tr>

              ))}

            </tbody>


      </div>
    )
  }
}
