import React, { Component } from "react";
import axios from 'axios'
import jsPDF from "jspdf";
//import lab from "../../models/lab";

export default class Home extends Component {

constructor(props){
  super(props);
this.state={
  lab:[]
};

}

componentDidMount(){
  this.retriveReports();
}

retriveReports(){
  axios.get("/lab").then(res =>{

  if(res.data.success){
    this.setState({
      lab:res.data.existingLab
    });

    console.log(this.state.lab)
  }

  });
}


 //jsPDF generator function
 jsPDFGenerator = () =>{
  //const id = this.props.match.params.id;
   //const name = this.props.match.params.name;
   //const name = this.props.match.params.name;
   //const {name,nic,age,sex,type,result,date,status} = this.state.lab;

   //new document in jspdf
   var doc = new jsPDF('p','pt');

   //add some text to pdf

   //doc.addImage("/img/sakura_logo.png", "PNG", 15,40,180,180)

   doc.text(200,40,`${type}   Report`)

   doc.text(100,120,'Name')
   doc.text(100,140,'NIC')
   doc.text(100,160,'Age')
   doc.text(100,180,'Sex')
   doc.text(100,200,'Test Type')
   doc.text(100,220,'Result')
   doc.text(100,240,'Date')
   doc.text(100,260,'Status')

   doc.text(300,120,name)
   doc.text(300,140,nic)
   doc.text(300,160,age)
   doc.text(300,180,sex)
   doc.text(300,200,type)
   doc.text(300,220,result)
   doc.text(300,240,date)
   doc.text(300,260,status)

   //set the font for pdf
   doc.setFont('courier');
   doc.setFontSize(10);

   //set the font type
   //doc.setFontType('normal')

   doc.text(150,400,'Tested by SAKURA Hospital Laboratory');

   //Save the pdf
   doc.save(`${nic}.pdf`)

}






onDelete = (id) => {
    axios.delete(`/lab/delete/${id}`).then((res) =>{
        alert("Deleted Successfully !!");
        this.retriveReports();
    })
}

filterData(lab,searchKey){
    const result = lab.filter((lab) =>
    lab.name.toLowerCase().includes(searchKey) ||
    lab.name.includes(searchKey) ||
    lab.nic.toLowerCase().includes(searchKey) ||
    lab.age.toLowerCase().includes(searchKey) ||
    lab.sex.toLowerCase().includes(searchKey) ||
    lab.sex.includes(searchKey) ||
    lab.type.toLowerCase().includes(searchKey) ||
    lab.type.includes(searchKey) ||
    lab.result.toLowerCase().includes(searchKey) ||
    lab.result.includes(searchKey) ||
    lab.date.toLowerCase().includes(searchKey) ||
    lab.status.toLowerCase().includes(searchKey) ||
    lab.status.includes(searchKey)
    )

    this.setState({lab:result});

}


handleSearchArea = (e) =>{
   const searchKey = e.currentTarget.value;

   axios.get("/lab").then(res =>{

    if(res.data.success){
      this.filterData(res.data.existingLab,searchKey)
    }

    });
}

  render() {
    return(
      <div className="container">
          <div className="row">
              <div className="col-lg-9 mt-2 mb-2">
                <h4>Laboratory Management System</h4>
              </div>
              <div className="col-lg-3 mt-2 mb-2">
                  <input 
                  className="form-control"
                  type="search"
                  placeholder="Search"
                  name="searchQuery"
                  onChange={this.handleSearchArea}>

                  </input>
              </div>
          </div>

          <button className="btn btn-success"><a href="/add" style={{textDecoration:'none',color:'white'}}>Add New +</a></button>

        <table className="table">
        <thead>
                <tr>
                    <th scope="col">ID</th>
                    <th scope="col">Name</th>
                    <th scope="col">NIC</th>
                    <th scope="col">Age</th>
                    <th scope="col">Sex</th>
                    <th scope="col">Type</th>
                    <th scope="col">Results</th>
                    <th scope="col">Date</th>
                    <th scope="col">Status</th>
                    <th scope="col">Action</th>
                </tr>
            </thead>
            <tbody>
              {this.state.lab.map((lab,index) =>(
                //<tr key={index}>
                <tr>
                  <th scope="row">{index+1}</th>
                    <td>
                        <a href={`/lab/${lab._id}`} style={{textDecoration:'none'}}>
                      {lab.name}
                      </a>
                    </td>
                  <td>{lab.nic}</td>
                  <td>{lab.age}</td>
                  <td>{lab.sex}</td>
                  <td>{lab.type}</td>
                  <td>{lab.result}</td>
                  <td>{lab.date}</td>
                  <td>{lab.status}</td>
                  <td>
                    <a className="btn btn-warning" href={`/edit/${lab._id}`}>
                      <i className="fas fa-edit"></i>&nbsp;Edit
                    </a>
                    &nbsp;
                    <a className="btn btn-danger" href="#" onClick={() =>this.onDelete(lab._id)}>
                      <i className="far fa-trash-alt"></i>&nbsp;Delete
                    </a>
                    &nbsp;
                    <a className="btn btn-success" href="#" onClick={this.jsPDFGenerator}>
                      <i className="fas fa-cogs"></i>&nbsp;Generate
                    </a>
                  </td>
                </tr>

              ))}

            </tbody>

            </table>



      </div>
    )
  }
}
